pimcore.helpers.addCsrfTokenToUrl = function (url) {

    // only for /admin urls
    if(url.indexOf('/admin') !== 0) {
        return url;
    }

    if (url.indexOf('?') === -1) {
        url = url + "?";
    } else {
        url = url + "&";
    }
    url = url + 'csrfToken=' + pimcore.settings['csrfToken'];

    return url;
};

